import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Header from "../../Template/Appro/Header";
import Tr_demandeProforma from "./Tr_demandeProforma";

function ListDemandeProforma() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch("http://127.0.0.1:3000/demandeproformaM")
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div>
        <Header />
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Date demande</th>
              <th scope="col">Date de livraison</th>
              <th scope="col">Lieu de livraison</th>
            </tr>
          </thead>
          <tbody>
            {items.map((d, index) => (
              <Tr_demandeProforma
                link={"proforma"}
                key={index}
                demande={d}
              ></Tr_demandeProforma>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

//export default App;
export default ListDemandeProforma;
