import React, { useState, useEffect } from "react";

const Details = ({
  rubriques,
  articles,
  rubrique,
  article,
  methode,
  qt,
  qualite,
  listMethode,
}) => {
  const listQualite = [
    "Made in China",
    "Made in Mada",
    "Made in France",
    "Made in USA",
  ];

  const [idArticle, setIdArticle] = useState(null);

  useEffect(() => {
    if (idArticle != null) {
    }
  }, [idArticle]);

  return (
    <div className="App">
      <h1>Details proforma</h1>

      <br />
      <div className="row">
        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Rubriques</label>
            <select id="select" className="form-control" ref={rubrique}>
              {rubriques.map((element, index) => (
                <option value={element.idrubrique} key={index}>
                  {element.nomrubrique}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Articles</label>
            <select
              className="form-control"
              ref={article}
              onClick={(e) => setIdArticle(e.target.value)}
            >
              {articles.map((element, index) => (
                <option value={element.idarticle_fournisseur} key={index}>
                  {element.nomarticle}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Quantite</label>
            <input className="form-control" type="number" ref={qt} />
          </div>
        </div>

        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Methode</label>
            <select className="form-control" ref={methode}>
              {listMethode.map((element, index) => (
                <option key={index} value={element.id}>
                  {element.nom}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Qualite</label>
            <select className="form-control" ref={qualite}>
              {listQualite.map((element, index) => (
                <option key={index} value={element}>
                  {element}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Details;
