import React, { useRef, useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";


import Info_proforma from "./info";
import Table_checkbox from "./table";
import Checkbox_btn from "./Checkbox_btn";
import Add_fournisseur from "./add_fournisseur";
import Header from "../../Template/Appro/Header";



function Envoye_demande({ list }) {

    const [listes_article_fixed, setArticles_fixed] = useState([]);
    const [fournisseurs_all, setFournisseurs_all] = useState([]);
    const [demande_proforma, setDemande_proforma] = useState([]);
    const navigate = useNavigate();

  const HandleForm = (event) => {

    var dateLivraison=date_livraison.current.value;
    var lieuLivraison=lieu_livraison.current.value;

    var articleDemande=[]
    for (let i = 0; i < article_demande.current.length; i++) {

      if(article_demande.current[i].checked)
        articleDemande.push({article_fixed_id: article_demande.current[i].value})

    }

    var fournisseurDemande=[]
    for (let i = 0; i < fournisseur_demande.current.length; i++) {

      fournisseurDemande.push({idfournisseur: fournisseur_demande.current[i].value})

    }

    var demandeProforma={demande_proformat: {date_livraison: dateLivraison,lieu_livraison: lieuLivraison},article_demande: articleDemande,fournisseur_demande: fournisseurDemande, idappro: 4}

    setDemande_proforma(demandeProforma)


  };

  useEffect(()=>{

    var content = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    };
    content.body = JSON.stringify(demande_proforma);


      fetch("http://127.0.0.1:3000/demandeProformat", content)
      .then(
        (response) => {
          if (response.status === 400) {
            alert("Erreur lors du demande de proforma");
          }
          return response.json()
        }
      )
      .then((json)=>{
        navigate('/email_interface',{state:{json}})
      })
  },[demande_proforma])



  useEffect(() => {
    var content = {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    };

    fetch("http://127.0.0.1:3000/giveDateDemandeProforma", content)
      .then((response) => {
        if (response.status === 400) {
          alert("Erreur");
        } else return response.json();
      })
      .then((json) => {
        setArticles_fixed(json.allArticleValides);
        setFournisseurs_all(json.allFournisseurs);
        // console.log(json.allArticleValides);
        // console.log(json.allFournisseurs)
      });
  }, []);


  const fournisseur_demande = useRef([]);
  const article_demande = useRef([]);
  const date_livraison = useRef();
  const lieu_livraison=useRef();

    return (
        <div className="container">

      <Header />
            <form method="POST">
                <div className="row">
                    <div className="col col-md-8">

                        <Info_proforma date_livraison={date_livraison} lieu_livraison={lieu_livraison} />

                        <Checkbox_btn />

                        <div className="table-responsive">
                            <Table_checkbox article_demande={article_demande} listes_article_fixed={listes_article_fixed} />
                        </div>
                    </div>
                    <div className="col col-md-4">

                        <Add_fournisseur fournisseur_demande={fournisseur_demande}  all_fournisseurs={fournisseurs_all}  />


                    </div>

                </div>
                <div className="row" style={{ "marginTop": "20px" }}>
                    <button type="button" onClick={HandleForm} className="btn btn-success">Valider</button>

                </div>
            </form>
        </div>
    );

}

export default Envoye_demande;
