import { useState } from "react";

// How should look like the front of ONE proformat
const ProformatFournisseur = (props) => {
    const [reference, setReference] = useState(props.details.ref);
    const [date_livraison, setDate_livraison] = useState(props.details.date_livraison);
    const [lieu_livraison,setLieu_livraison] = useState(props.details.lieu_livraison);
    // a json object 
        // the first index is its id 
        // the second its name
    const [mode_livraison,setMode_livraison] = useState([props.details.mode_livraison_id,props.details.nommode_livraison]);
    const [acompte, setAcompte] = useState(props.details.acompte);


    return(
        <div className="container">
            <div className="row">
                <h2>Reference : {reference}</h2>
                <h3>Date livraison : {date_livraison}</h3>
                <h3>Lieu livraison : {lieu_livraison}</h3>
            </div>

            <div className="row">
                <p>Mode livraison : {mode_livraison[1]}</p>
                <p>Acompte : {acompte} </p>
            </div>
        </div>
    );
}

export default ProformatFournisseur;
