import React, { useRef, useState, useEffect } from "react";
import Header from "../../Template/Departement/Header";
import Card from "./Card";

const DemandeAchat = () => {
  const [listrubrique, setListrubrique] = useState([]);
  const [articles, setArticles] = useState([]);
  const [unites, setUnites] = useState([]);
  const [cards, setCards] = useState([]);
  const [demandeAchat, setDemandeAchat] = useState([]);
  const rubrique = useRef([]);
  const nomarticle = useRef([]);
  const quantite = useRef([]);
  const unite = useRef([]);
  const description = useRef([]);
  const [has, setHas] = useState(true)

  class DemandeAchat {
    idrubrique;
    nom_article;
    qtt;
    unite_id;
    qualite;
    idarticle;
  }

  const handleClick = (event) => {
    setCards([...cards, ""]);
    setHas(false);
  };

  useEffect(() => {
    var content = {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    };

    fetch("http://127.0.0.1:3000/getDataDemanderAchat", content)
      .then((response) => {
        if (response.status === 400) {
          alert("Mot de passe non identifier");
        } else return response.json();
      })
      .then((json) => {
        setListrubrique(json.allRubrique);
        setArticles(json.allProduit);
        setUnites(json.allUnite);
        console.log(json);
      });
  }, []);

  useEffect(() => {
    var content = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    };
    content.body = JSON.stringify({ article: demandeAchat });

    if (demandeAchat.length !== 0) {
      fetch("http://127.0.0.1:3000/demanderAchatv", content).then(
        (response) => {
          if (response.status === 400) {
            alert("Erreur lors du demande d'Achat");
          }
        }
      );
    }
  }, [demandeAchat]);

  const handleForm = (event) => {
    var idarticle = [];
    for (var i = 0; i < description.current.length; i++) {
      var id = 0;
      for (var j = 0; j < articles.length; j++) {
        if (nomarticle.current[i].value === articles[j].nom_article)
          id = articles[j].idarticle;
      }

      idarticle.push(id);
    }
    var value = [];

    for (let i = 0; i < description.current.length; i++) {
      var newDemandeAchat = new DemandeAchat();
      newDemandeAchat.idrubrique = rubrique.current[i].value;
      newDemandeAchat.qtt = quantite.current[i].value;
      newDemandeAchat.unite_id = unite.current[i].value;
      newDemandeAchat.qualite = description.current[i].value;
      newDemandeAchat.nom_article = nomarticle.current[i].value;
      newDemandeAchat.idarticle = idarticle[i];
      value.push(newDemandeAchat);
    }
    setDemandeAchat(value);
  };

  return (
    <div>
      <Header />
      <div className="page-section">
        <div className="container">
          <div className="row justify-content-center">
            <div className="text-center">
              <h1>
                <span className="text-secondary">Demande D'achats</span>
              </h1>
            </div>
          </div>
          <div className="row">
            <div className="col-lg-3">
              <button
                className="form-submit btn btn-outline-primary"
                type="button"
                onClick={handleClick}
              >
                Ajouter
              </button>
            </div>
          </div>

          <form className="form-group">
            <div className="row">
              {cards.map((element, index) => (
                <Card
                  key={index}
                  index={index}
                  rubrique={rubrique}
                  quantite={quantite}
                  unite={unite}
                  description={description}
                  nomarticle={nomarticle}
                  listrubrique={listrubrique}
                  articles={articles}
                  unites={unites}
                />
              ))}
            </div>

            <div hidden={has} className="row justify-content-center">
              <button
                className="form-submit btn btn-success"
                type="button"
                onClick={handleForm}
              >
                Confirmer
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default DemandeAchat;
