import { useState,useEffect, Fragment } from "react";
import {Link, useParams} from "react-router-dom";
import Header from "../../Template/Appro/Header";

function ListDemandeProformatClient() {

  const {iddemande} = useParams();
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch("http://127.0.0.1:3000/listeproforma?iddemande="+iddemande)
      .then(res => res.json())
      .then(
        (result) => {

          setIsLoaded(true);
          setItems(result);

        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <>
      <Header  />
      <div>

            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Ref proforma</th>
                        <th scope="col">Fournisseur</th>
                        <th scope="col">prix estimé</th>
                        <th scope="col">prix avant</th>
                        <th scope="col">Note qualité</th>
                        <th scope="col">Note livraison</th>
                    </tr>
                </thead>
                <tbody>
            {
              items.map( (element,index) => (
                <tr key={index}>
                  <td>{element.ref_proformat}</td>
                  <td>{element.fournisseur}</td>
                  <td>{element.prix_estime}</td>
                  <td>{element.prix_avant}</td>
                  <td>{element.qualite_note}</td>
                  <td>{element.livraison_note}</td>
                  <td><Link to={'/bonCommande/'+element.idproformat}>Aperçu bon de commande</Link></td>
                </tr>
              ))
            }
                </tbody>
            </table>
      </div>
      </>
    );
  }
}

export default ListDemandeProformatClient;
