import React, { useState, useEffect } from "react";
import { Popup } from "./Popup";

const Details = ({
  rubriques,
  articles,
  rubrique,
  article,
  methode,
  qt,
  setArticles,
  qualite,
  listMethode,
}) => {
  const listQualite = [
    "Made in China",
    "Made in Mada",
    "Made in France",
    "Made in USA",
  ];

  const [idArticle, setIdArticle] = useState(null);
  const [visible, setVisible] = useState(false);
  const [methodearticle, setMethodearticle] = useState();
  const popupCloseHandler = (e) => {
    setVisible(e);
  };

  useEffect(() => {
    if (idArticle != null) {
      const articleSelected = articles.filter(
        (element) => element.idarticle_fournisseur == idArticle
      )[0];
      console.log(articleSelected);

      if (articleSelected.idmethode_article == null) {
        setVisible(visible == false ? true : true);
      } else {
        methode.current = articleSelected.methode_id;
        setVisible(false);
      }
    }
  }, [idArticle]);

  const handleCreate = () => {
    const methodeSeleted = listMethode.filter(
      (e) => e.idmethode === parseInt(methodearticle)
    )[0];

    methode.current = methodeSeleted.idmethode;

    var content = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    };

    content.body = JSON.stringify({
      idmethode: methodeSeleted.idmethode,
      idarticle: idArticle,
    });
    console.log(content);
    fetch("http://127.0.0.1:3000/newarticlemethode", content)
      .then((response) => response.json())
      .then((json) => {
        console.log(json);
        setArticles(json.articles);
      });
    setVisible(!visible);
  };

  return (
    <div className="App">
      <h1>Details proforma</h1>

      <br />
      <div className="row">
        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Rubriques</label>
            <select id="select" className="form-control" ref={rubrique}>
              {rubriques.map((element, index) => (
                <option value={element.idrubrique} key={index}>
                  {element.nomrubrique}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Articles</label>
            <select
              className="form-control"
              ref={article}
              onClick={(e) => setIdArticle(e.target.value)}
            >
              {articles.map((element, index) => (
                <option value={element.idarticle_fournisseur} key={index}>
                  {element.nomarticle}
                </option>
              ))}
            </select>
          </div>

          <Popup
            onClose={popupCloseHandler}
            show={visible}
            title="Ajout methode"
          >
            <select
              className="form-control"
              onClick={(e) => setMethodearticle(e.target.value)}
            >
              {listMethode.map((element, index) => (
                <option key={index} value={element.idmethode}>
                  {element.nom_methode}
                </option>
              ))}
            </select>
            <input
              className="btn btn-primary"
              type="button"
              value="Ajouter"
              onClick={handleCreate}
            />
          </Popup>
        </div>

        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Quantite</label>
            <input className="form-control" type="number" ref={qt} />
          </div>
        </div>

        <div className="col-sm-2">
          <div className="form-group mb-3">
            <label className="form-label">Qualite</label>
            <select className="form-control" ref={qualite}>
              {listQualite.map((element, index) => (
                <option key={index} value={element}>
                  {element}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Details;
